INSERT INTO `vi_album` VALUES ('8', '1', null, ',53,', '19 HÌNH ẢNH PHONG CẢNH ĐÀ LẠT TUYỆT ĐẸP', null, 'Phải nói Đà Lạt là thành phố mộng mơ với nhiều phong cảnh lãng mạn và đẹp mê lòng người. Du khách đã đến đây rồi thì khó có thể quên hình ảnh đẹp tuyệt vời của thành phố này.', 'uploadv2/web/1/1/album/2015/12/22/04/20/1450758017_hinh-anh-da-lat-dep-nhat-8.jpg', '11561', '1', '310', '310', '2015', '2015', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '1', '0', '19-hinh-anh-phong-canh-da-lat-tuyet-dep');
INSERT INTO `vi_album` VALUES ('9', '1', null, ',53,', 'ĐÀ LẠT, THÀNH PHỐ MỘNG MƠ', null, 'Khi đặt chân lên Đà Lạt nằm trên cao nguyên Langbian, du khách sẽ không khỏi ngạc nhiên đến ngỡ ngàng trước cảnh sắc thiên nhiên với rừng thông trùng điệp, thác đổ ào ào, suối tuôn róc rách và đặc biệt là các loài hoa.', 'uploadv2/web/1/1/album/2015/12/22/04/29/1450758565_hinh-anh-da-lat-14.jpg', '11579', '1', '310', '310', '2015', '2015', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '1', '0', 'da-lat-thanh-pho-mong-mo');
INSERT INTO `vi_album` VALUES ('10', '1', null, ',53,', 'HÌNH ẢNH ĐẸP TUYỆT VỀ ĐÀ LẠT TRƯỚC 1975', null, 'Nhiều hình ảnh đẹp về Đà Lạt trước 1975 nhìn từ máy bay của tác giả Bill Robie đã được chia sẻ trên một website của cựu quân nhân không lực Mỹ.', 'uploadv2/web/1/1/album/2015/12/22/04/50/1450759806_hinh-anh-dep-tuyet-ve-da-lat-truoc-1975-nhin-tu-ma.jpg', '11588', '1', '310', '310', '2015', '2015', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '1', '0', 'hinh-anh-dep-tuyet-ve-da-lat-truoc-1975');
INSERT INTO `vi_album` VALUES ('11', '1', null, ',53,', 'ĐÀ LẠT TUYỆT ĐẸP TRÊN BƯU THIẾP TRĂM TUỔI', null, 'Nhìn từ trên cao, Đà Lạt thời thuộc địa hiện ra như một chốn địa đàng giữa vẻ hoang vu của núi rừng Tây Nguyên...', 'uploadv2/web/1/1/album/2015/12/22/05/04/1450760671_ngam-da-lat-tuyet-dep-tren-buu-thiep-tram-tuoi-1-h.jpg', '11598', '1', '310', '310', '2015', '2015', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'ngam-da-lat-tuyet-dep-tren-buu-thiep-tram-tuoi');
INSERT INTO `vi_album` VALUES ('12', '1', null, ',53,', 'NGẮM SÂN BAY ĐÀ LẠT NGẬP TRÀN HOA DÃ QUỲ', null, 'Đây là thời điểm hoa dã quỳ nở vàng những con đường, lối đi. Khung cảnh sân bay Đà Lạt cũng thêm thơ mộng vì loài hoa này.', 'uploadv2/web/1/1/album/2015/12/22/06/48/1450766920_ngat-ngay-ngam-san-bay-da-lat-ngap-tran-hoa-da-quy.jpg', '11618', '1', '310', '310', '2015', '2015', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'ngat-ngay-ngam-san-bay-da-lat-ngap-tran-hoa-da-quy');
INSERT INTO `vi_album` VALUES ('13', '1', null, ',53,', 'DINH THỰ LỘNG LẪY CỦA VUA BẢO ĐẠI TẠI ĐÀ LẠT', null, 'Dinh thự của vua Bảo Đại tại Đà Lạt vô cùng sang trọng, xa hoa, có đường hầm bí mật, bãi đậu trực thăng... và nằm giữa khu rừng thông bạt ngàn.', 'uploadv2/web/1/1/album/2015/12/22/07/01/1450767661_tham-dinh-thu-long-lay-cua-vua-bao-dai-tai-da-lat-_2.jpg', '11634', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'dinh-thu-long-lay-cua-vua-bao-dai-tai-da-lat');
INSERT INTO `vi_album` VALUES ('14', '1', null, ',53,', 'PHƯỢT ĐÀ LẠT MÙA SĂN MÂY ĐẸP HUYỀN ẢO', null, 'Tháng 6 là mùa săn mây ở Đà Lạt. Đó là mùa mây luồng (hay biển mây), thường bắt đầu khi hết mùa nắng lạnh để chuyển sang mùa sương bạc.', 'uploadv2/web/1/1/album/2015/12/22/07/18/1450768704_phuot-da-lat-mua-san-may-dep-huyen-ao.jpg', '11648', '1', '310', '310', '2015', '2015', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '1', '1', 'phuot-da-lat-mua-san-may-dep-huyen-ao');
INSERT INTO `vi_album` VALUES ('15', '1', null, ',53,', 'MIỀN MỘNG MƠ ĐÀ LẠT 1992', null, 'Hai thập kỷ, hoạt động du lịch vẫn chưa phát triển sôi động ở Đà Lạt. Thành phố này toát lên vẻ thanh bình và thơ mộng quyến rũ lòng người.', 'uploadv2/web/1/1/album/2015/12/22/07/31/1450769508_kienthuc-da-lat-grumpe-03_riqj.jpg', '11666', '1', '310', '310', '2015', '2015', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'da-lat-the-city-of-dreams-in-1992');
INSERT INTO `vi_album` VALUES ('16', '1', null, ',53,', 'ĐÀ LẠT ĐẸP HƠN PHIM NHỜ MAI ANH ĐÀO', null, 'Đầu xuân cũng là thời điểm hàng nghìn gốc mai anh đào trên khắp các tuyến đường và khuôn viên các gia đình ở Đà Lạt bung nở, nhuộm hồng rực rỡ.', 'uploadv2/web/1/1/album/2015/12/22/07/39/1450769970_da-lat-dep-hon-phim-nho-mai-anh-dao-hinh-2.jpg', '11674', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '1', '0', 'da-lat-dep-hon-phim-nho-mai-anh-dao');
INSERT INTO `vi_album` VALUES ('17', '1', null, ',53,', 'NGẮM BIỆT THỰ ĐÁ CỔ ĐẸP NHẤT ĐÀ LẠT', null, 'Đà Lạt với những kiến trúc cổ độc đáo, là sự kết hợp hài hòa Đông Phương và Tây Âu, giữa lòng thành phố tráng lệ vẫn còn đó những biệt thự đá cổ, là nét riêng biệt làm nên Đà Lạt.', 'uploadv2/web/1/1/album/2015/12/22/07/43/1450770236_nha-da-co-dep-nhat-da-lat-5.jpg', '11689', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'ngam-biet-thu-da-co-dep-nhat-da-lat');
INSERT INTO `vi_album` VALUES ('19', '1', null, ',53,', 'ẢNH MÀU “ĐỘC VÀ ĐẸP” VỀ ĐÀ LẠT NĂM 1965', null, 'Loạt ảnh màu về Đà Lạt cách đây nửa thế kỷ do nhà sưu tầm người Mỹ Warren G.Reed giới thiệu đã tái hiện vẻ đẹp nguyên sơ của thành phố này...', 'uploadv2/web/1/1/album/2015/12/22/08/16/1450772166_kienthuc-da-lat-1965-01.jpg', '11695', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'anh-mau-doc-va-dep-ve-da-lat-nam-1965');
INSERT INTO `vi_album` VALUES ('20', '1', null, ',53,', 'PHƯỢT ĐÀ LẠT ĐÓN MÙA DÃ QUỲ NỞ HOA VÀNG RỰC', null, 'Đà Lạt là thành phố của ngàn hoa, nhưng nhắc đến vùng đất này, nhiều người sẽ vẫn nhớ đến những mùa hoa dã quỳ vàng ươm, nở rộ.', 'uploadv2/web/1/1/album/2015/12/22/08/23/1450772624_dalatkienthuc7_yple.jpg', '11731', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'phuot-da-lat-don-mua-da-quy-no-hoa-vang-ruc');
INSERT INTO `vi_album` VALUES ('22', '1', null, ',53,', 'KHÁM PHÁ LÀNG CÙ LẦN ĐẸP NHƯ TRANH GIỮA NÚI RỪNG LANGBIANG', null, 'Ngôi làng có cái tên vô cùng độc đáo này là một trong những địa điểm du lịch mới nổi của Đà Lạt.', 'uploadv2/web/1/1/album/2015/12/22/08/32/1450773165_langkt_hyob.jpg', '11750', '1', '310', '310', '2015', '2015', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'kham-pha-lang-cu-lan-dep-nhu-tranh-giua-nui-rung-langbiang');
INSERT INTO `vi_album` VALUES ('23', '1', null, ',53,', 'NHÀ ĐẸP ĐÀ LẠT NGẬP TRONG SƯƠNG MÙ GIỮA MÙA HẠ', null, 'Ngay giữa mùa hè, người ta vẫn thấy sương mù bay lơ đãng sà cả vào những nhà đẹp bằng gỗ có kiến trúc rất lạ trên lưng chừng đồi.', 'uploadv2/web/1/1/album/2015/12/22/08/37/1450773473_anh-4_lzje.jpg', '11767', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'nha-dep-da-lat-ngap-trong-suong-mu-giua-mua-ha');
INSERT INTO `vi_album` VALUES ('24', '1', null, ',53,', 'BÍ ẨN BIỆT THỰ ĐÁ KHỦNG BẢO ĐẠI MUA TẶNG TÌNH NHÂN', null, 'Với nhiều điểm đặc biệt, tòa biệt thự trở thành độc nhất vô nhị trong số hàng trăm ngôi biệt thự được xây dựng từ thời Pháp ở Đà Lạt.', 'uploadv2/web/1/1/album/2015/12/22/08/43/1450773811_kienthuc-biet-thu-phi-anh-04_msod_jync.jpg', '11772', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":1,\"show_quantity\":5,\"related_order\":1}', null, '0', '0', '', '0', '1', 'bi-an-biet-thu-da-khung-bao-dai-mua-tang-tinh-nhan');
INSERT INTO `vi_album` VALUES ('25', '1', null, ',53,', 'DINH THỰ CỔ CÓ PHONG THỦY ĐẸP NHẤT ĐÀ LẠT', null, 'Tòa dinh thự này nằm trên một ngọn đồi cao nhất ngay giữa trung tâm thành phố, được giới phong thủy xem là “cao điểm long mạch” của Đà Lạt.', 'uploadv2/web/1/1/album/2015/12/22/08/53/1450774439_kienthuc-dinh-tinh-truong-02_bimb_ipke.jpg', '11781', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'dinh-thu-co-co-phong-thuy-dep-nhat-da-lat');
INSERT INTO `vi_album` VALUES ('26', '1', null, ',53,', 'MÊ MẨN TRƯỜNG NỮ SINH QUÝ TỘC CỦA NAM PHƯƠNG HOÀNG HẬU', null, 'Đối tượng theo học ở trường chủ yếu là nữ sinh con nhà giàu có của các gia đình người Pháp, Việt Nam, Campuchia và Lào.', 'uploadv2/web/1/1/album/2015/12/22/09/03/1450774987_redsvn-truong-dtnt-01_fktd.jpg', '11798', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'me-man-truong-nu-sinh-quy-toc-cua-nam-phuong-hoang-hau');
INSERT INTO `vi_album` VALUES ('27', '1', null, ',53,', 'CHIÊM NGƯỠNG NHÀ THỜ CON GÀ NỔI TIẾNG NHẤT ĐÀ LẠT', null, 'Nhà thờ Con Gà là một trong những công trình nổi bật, có thể quan sát được từ rất nhiều vị trí khác nhau của thành phố Đà Lạt.', 'uploadv2/web/1/1/album/2015/12/22/09/10/1450775401_kienthuc-nha-tho-con-ga-01_jnfw.jpg', '11804', '1', '310', '310', '2015', '2015', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '1', '0', 'chiem-nguong-nha-tho-con-ga-noi-tieng-nhat-da-lat');
INSERT INTO `vi_album` VALUES ('28', '1', null, ',53,', 'VẺ ĐỘC ĐÁO CỦA “CHÙA TÀU” GIỮA XỨ ĐÀ LẠT', null, 'Thiên Vương Cổ Sát là một ngôi chùa độc đáo nằm trên đồi Rồng, cách trung tâm thành phố Đà Lạt khoảng 5km về phía Đông Bắc...', 'uploadv2/web/1/1/album/2015/12/22/09/27/1450776425_kienthuc-chua-tau-04_jbwf.jpg', '11820', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 've-doc-dao-cua-chua-tau-giua-xu-da-lat');
INSERT INTO `vi_album` VALUES ('29', '1', null, ',53,', 'TẬN MỤC NHÀ THỜ CÓ 1-0-2 Ở ĐÀ LẠT', null, 'Phục vụ đối tượng là người dân tộc thiểu số, kiến trúc của nhà thờ Cam Ly mang một sắc thái độc đáo, khác hẳn với các nhà thờ dành cho người Kinh.', 'uploadv2/web/1/1/album/2015/12/22/10/15/1450779313_kienthuc-nha-tho-cam-ly-02_ztpy.jpg', '11833', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'tan-muc-nha-tho-co-1-0-2-o-da-lat');
INSERT INTO `vi_album` VALUES ('30', '1', null, ',53,', 'KIẾN TRÚC KHÔNG ĐỤNG HÀNG LẦN ĐẦU XUẤT HIỆN Ở ĐÀ LẠT', null, 'Loại kiến trúc mới xuất hiện tại Đà Lạt khiến người chứng kiến phải thốt lên ngỡ ngàng vì đẹp.', 'uploadv2/web/1/1/album/2015/12/22/10/33/1450780400_1_hesl.jpg', '11835', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '1', '0', 'kien-truc-khong-dung-hang-lan-dau-xuat-hien-o-da-lat');
INSERT INTO `vi_album` VALUES ('32', '1', null, ',53,', 'PHẬT ĐẢN ĐÀ LẠT 2014', null, 'Lễ Phật Đản sinh vô cùng trang trọng và hoành tráng với hoa, đèn... Với sự tham gia hầu hết các Chùa trên địa bàn Thành phố Đà Lạt', 'uploadv2/web/1/1/album/2015/12/22/11/00/1450782027_img_2143.jpg', '11882', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '1', '0', 'phat-dan-da-lat-2014');
INSERT INTO `vi_album` VALUES ('33', '1', null, ',53,', 'TẬN MẮT TRỒNG DÂU TÂY ĐỈNH CAO CHỈ CÓ Ở ĐÀ LẠT', null, 'Ở Đà Lạt (Lâm Đồng), hoạt động nông nghiệp đã lên đến đỉnh cao về áp dụng khoa học kỹ thuật, nhất là trồng dâu tây trong nhà kính.', 'uploadv2/web/1/1/album/2015/12/22/12/38/1450787891_1_bqxu.jpg', '11916', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'tan-mat-trong-dau-tay-dinh-cao-chi-co-o-da-lat');
INSERT INTO `vi_album` VALUES ('34', '1', null, ',53,', 'HOA DÃ QUỲ NỞ NỘ, DÂN PHƯỢT LẠI MỘT PHEN LÊN ĐƯỜNG', null, 'Hoa dã quỳ đã nở trải vàng khắp nhiều miền sơn cước, thảo nguyên như Bà Vì, Đà Lạt... thôi thúc những bạn trẻ xách ba lô lên và đi.', 'uploadv2/web/1/1/album/2015/12/22/12/45/1450788351_hoa-da-quy-no-no-dan-phuot-lai-mot-phen-len-duong-_2.jpg', '11938', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'hoa-da-quy-no-no-dan-phuot-lai-mot-phen-len-duong');
INSERT INTO `vi_album` VALUES ('35', '1', null, ',53,', 'BỜ RÀO ĐẸP NHƯ TRANH CỦA NHÀ ĐÀ LẠT', null, 'Những bờ rào lãng mạn càng khiến biệt thự hay tòa nhà công sở ở Đà Lạt thêm hoàn hảo.', 'uploadv2/web/1/1/album/2015/12/22/12/52/1450788756_1_lbmc.jpg', '11955', '1', '310', '310', '2015', '2015', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'bo-rao-dep-nhu-tranh-cua-nha-da-lat');
INSERT INTO `vi_album` VALUES ('36', '1', null, ',53,', 'BIỆT THỰ ĐẠI GIA NÀO TO, ĐẸP NHẤT ĐÀ LẠT?', null, 'Hàng ngàn căn biệt thự của các đại gia Đà Lạt được xây dựng theo lối kiến trúc cực kỳ hiện đại khiến người ngắm nhìn phải suýt xoa trầm trồ.', 'uploadv2/web/1/1/album/2015/12/22/12/59/1450789160_1_klzw.jpg', '11973', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'biet-thu-dai-gia-nao-to-dep-nhat-da-lat');
INSERT INTO `vi_album` VALUES ('37', '1', null, ',53,', 'NHÀ ĐẤT SÉT CÓ MỘT KHÔNG HAI CỦA ĐẠI GIA ĐÀ LẠT', null, 'Căn nhà bằng đất sét đỏ đặc trưng của vùng đất cao nguyên đã mọc lên giữa rừng thông thơ mộng ở hồ Tuyền Lâm - Đà Lạt đẹp đến ngỡ ngàng.', 'uploadv2/web/1/1/album/2015/12/23/10/24/1450866274_da-lat-5-6504-1402566777.jpg', '12287', '1', '310', '310', '2015', '2015', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '1', '0', 'nha-dat-set-co-mot-khong-hai-cua-dai-gia-da-lat');
INSERT INTO `vi_album` VALUES ('38', '1', null, ',53,', 'CẬN CẢNH VƯỜN CÀ CHUA TRÁI “KHỦNG” Ở ĐÀ LẠT', null, 'Lần đầu tiên trên thị trường Việt Nam xuất hiện loại cà chua trái &quot;khủng&quot; do chính những nhà vườn tại huyện Lạc Dương (Lâm Đồng) sản xuất.', 'uploadv2/web/1/1/album/2015/12/22/01/17/1450790222_kinhdoanh-kienthuc-10_bpdu.jpg', '12004', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'can-canh-vuon-ca-chua-trai-khung-o-da-lat');
INSERT INTO `vi_album` VALUES ('39', '1', null, ',53,', 'FESTIVAL HOA ĐÀ LẠT', null, 'Festival Hoa Đà Lạt là một sự kiện lễ hội được tổ chức hai năm một lần vào tháng 12 - tháng Đà Lạt có thời tiết đẹp nhất trong năm tại thành phố Đà Lạt, Lâm Đồng, Việt Nam và một số địa phương khác trong tỉnh Lâm Đồng. Festival Hoa là dịp để thành phố này', 'uploadv2/web/1/1/album/2015/12/22/01/37/1450791444_xahoi-kienthuc-14_hbxo.jpg', '12020', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'festival-hoa-da-lat');
INSERT INTO `vi_album` VALUES ('40', '1', null, ',53,', 'GÓC ẢNH ĐÀ LẠT', null, 'Tổng hợp ảnh chụp các địa điểm của Đà Lạt của anh em phượt thủ khắp cả nước. Không hiểu sao đang ở Đà Lạt nhưng mỗi khi nhìn các hình ảnh chụp quê hương mình lòng tôi lại nhói lên niềm yêu thương kỳ lạ.', 'uploadv2/web/1/1/album/2015/12/22/02/20/1450794023_8568304100_4eca4e20e4_c.jpg', '12171', '1', '310', '310', '2015', '2015', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '1', '0', 'goc-anh-da-lat');
INSERT INTO `vi_album` VALUES ('41', '1', null, ',53,', 'HOA HƯỚNG DƯƠNG ĐẸP NGẤT NGÂY', null, 'Ngay khi mùa hoa Dã quỳ vừa kết thúc thì nhiều ngày qua, những cánh đồng hoa Hướng dương và ruộng Cải lại tiếp tục khoe sắc, thôi thúc người ta lại tìm đến xứ sở ngàn hoa này. Trong đó đặc biệt nhất là cánh đồng hoa Hướng Dương rộng bạt ngàn ở huyện Đơn D', 'uploadv2/web/1/1/album/2015/12/23/03/53/1450842837_img_8261-35aba.jpg', '12192', '1', '310', '310', '2015', '2015', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '1', '0', 'canh-dong-hoa-huong-duong-dep-ngat-ngay-tai-da-lat');
INSERT INTO `vi_album` VALUES ('42', '1', null, ',53,', 'BÌNH MINH ĐẸP HƠN TRONG TRANH', null, 'Đến Đà Lạt, hẳn điều đầu tiên chúng ta nghĩ đến là những rừng thông bạt ngàn, những vườn hoa, nắng hanh hao miền cao nguyên và cơn rét ngọt mỗi khi chập choạng tối.', 'uploadv2/web/1/1/album/2015/12/23/04/20/1450844414_11-52906.jpg.jpg', '12216', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'binh-minh-dep-hon-trong-tranh');
INSERT INTO `vi_album` VALUES ('43', '1', null, ',53,', 'VƯỜN HOA BAN, HOA CẢI TRẮNG ĐẸP MÊ MẨN', null, 'Những ngày tháng 12, trời Đà Lạt có những hôm nắng rực rỡ, những vạt hoa cải trắng ngút mắt dưới chân, những cây ban nở đầy hoa mảnh mai in sắc trắng lên nền trời.', 'uploadv2/web/1/1/album/2015/12/23/04/46/1450845960_chum-anh-ngam-mua-hoa-ban-hoa-cai-trang-dep-me-man_2.jpg', '12234', '1', '310', '310', '2015', '2015', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'vuon-hoa-ban-hoa-cai-trang-dep-me-man');
INSERT INTO `vi_album` VALUES ('44', '1', null, ',53,', 'ĐÀO NỞ RỘ TẠI PHỐ NÚI ĐÀ LẠT', null, 'Chẳng cần đi đâu xa, tới lục địa nào cả, chỉ cần ở ngay những nẻo đường nước Việt Nam mình, bạn vẫn có thể ngắm nhìn những mùa hoa đẹp đến nao lòng.', 'uploadv2/web/1/1/album/2015/12/23/04/59/1450846774_trong-veo-nhu-nhung-canh-dao-no-ro-tai-pho-nui-da-.jpg', '12236', '1', '310', '310', '2015', '2015', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '1', '0', 'trong-veo-nhu-nhung-canh-dao-no-ro-tai-pho-nui-da-lat');
INSERT INTO `vi_album` VALUES ('45', '1', null, ',53,', 'ĐỒI CỎ HỒNG ĐẸP NHƯ TRANH', null, 'Ngoài hoa cải trắng và hướng dương, tháng 12 này Đà Lạt còn thu hút du khách bởi đồi cỏ hồng hoang sơ và lãng mạn.', 'uploadv2/web/1/1/album/2015/12/23/07/00/1450854001_9-1449197951_1200x0.jpg', '12250', '1', '310', '310', '2015', '2015', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'doi-co-hong-dep-nhu-tranh');
INSERT INTO `vi_album` VALUES ('46', '1', null, ',53,', 'BÌNH MINH ĐÀ LẠT ĐẸP MÊ HỒN', null, 'Đà Lạt đẹp trong cái se lạnh của sớm mai, đẹp trong cái khắc khoải đợi chờ ánh bình minh buổi tinh mơ, đẹp cả khi Đà Lạt chìm trong làn sương mỏng manh như gió.', 'uploadv2/web/1/1/album/2015/12/23/08/28/1450859312_da-lat-17-4718-1430901929.jpg', '12264', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'binh-minh-da-lat-dep-me-hon');
INSERT INTO `vi_album` VALUES ('47', '1', null, ',53,', 'CÁNH ĐỒNG TUYẾT SIÊU LÃNG MẠN', null, 'Nắm tay người thương đi trên cánh đồng tuyết trắng xóa luôn là ước mơ của bao nhiêu cô gái. Giờ đây ước mơ đó đã có thể trở thành hiện thực.', 'uploadv2/web/1/1/album/2015/12/23/10/09/1450865387_20151215-054312-8_600x389.jpg', '12269', '1', '310', '310', '2015', '2015', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '1', '0', 'canh-dong-tuyet-sieu-lang-man');
INSERT INTO `vi_album` VALUES ('48', '1', null, ',53,', 'CÂY TÌNH YÊU', null, 'Mỗi dải lụa hồng - đỏ vắt lên thân cây là một lời nguyện ước về tình yêu và tài lộc.', 'uploadv2/web/1/1/album/2015/12/23/10/18/1450865892_tinh-yeu-5-559574-1368319852_500x0.jpg', '12282', '1', '310', '310', '2015', '2015', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '0', '1', 'cay-tinh-yeu');
INSERT INTO `vi_album` VALUES ('49', '1', null, ',53,', 'LẠC VÀO XỨ SỞ HOA ĐÀ LẠT', null, 'Dường như ở Đà Lạt, hoa mọc khắp nơi, từ dải phân cách đường tới các hàng rào và nhiều nhất là ở các công viên hay khu du lịch. Bạn có thể thỏa sức ngắm nhìn hoa mai anh đào, dã quỳ, bồ công anh hay các loại hoa hồng, hoa cúc...', 'uploadv2/web/1/1/album/2015/12/23/10/57/1450868268_ve-da-lat-mua-xuan-ngo-nhu-lac-buoc-mot-thien-duon_5.jpg', '12357', '1', '310', '310', '2015', '2016', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '1', '0', 'lac-vao-xu-so-hoa-da-lat');
INSERT INTO `vi_album` VALUES ('50', '1', null, ',53,', 'LẠC BƯỚC NHỮNG NGÀY ĐÔNG ĐÀ LẠT', null, 'Vẫn nơi chốn ta muốn đi về khi lòng bộn bề những nỗi niềm chẳng rõ, vẫn một cõi riêng tư mà ta gửi vào đó cả những buồn vui thường nhật trong đời, Đà Lạt trong trái tim của kẻ lữ hành cứ mãi xốn xang những ưu tư như thế, để như một chốn dừng chân khi ta m', 'uploadv2/web/1/1/album/2015/12/23/11/07/1450868821_du-lich-da-lat-mua-dong-1.jpg', '12358', '1', '310', '310', '2015', '2016', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '0', '', '1', '0', 'lac-buoc-nhung-ngay-dong-da-lat');
INSERT INTO `vi_album` VALUES ('51', '1', null, ',54,', 'ẢNH CƯỚI BỐN MÙA TUYỆT ĐẸP Ở ĐÀ LẠT', null, 'Đà Lạt là địa điểm chụp ảnh cưới lý tưởng cho các uyên ương thích sự lãng mạn, bởi phong cảnh thiên nhiên ngập tràn sắc hoa cỏ. Dù ở mùa nào, Đà Lạt cũng có nét đẹp riêng.', 'uploadv2/web/1/1/album/2015/12/23/02/05/1450879523_3-3-1435373455_1200x0.jpg', '12462', '1', '310', '310', '2015', '2016', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '1', '', '1', '0', 'anh-cuoi-bon-mua-tuyet-dep-o-da-lat');
INSERT INTO `vi_album` VALUES ('52', '1', null, ',54,', 'ẢNH CƯỚI ĐẸP NHƯ MƠ VỚI HOA ANH ĐÀO', null, 'Những ngày đầu xuân, Đà Lạt mờ sương như khoác thêm tấm áo mới màu hồng phấn ngọt ngào và rạng rỡ của hoa mai anh đào. Vẻ đẹp dịu dàng của loài hoa này đã khiến bao trái tim du khách phải ngẩn ngơ...', 'uploadv2/web/1/1/album/2015/12/23/02/23/1450880624_chup-anh-cuoi-dep-nhu-mo-voi-hoa-mai-anh-dao-da-la.jpg', '12489', '1', '310', '310', '2015', '2015', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '1', '', '0', '1', 'anh-cuoi-o-da-lat-dep-nhu-mo-voi-hoa-anh-dao');
INSERT INTO `vi_album` VALUES ('53', '1', null, ',54,', 'ẢNH CƯỚI TUYỆT ĐẸP GIỮA MÙA HOA ĐÀ LẠT', null, 'Đôi uyên ương thực hiện những tấm ảnh cưới lãng mạn, hạnh phúc trong không gian của hoa cải trắng, hoa hướng dương và hoa dã quỳ của thành phố Đà Lạt.', 'uploadv2/web/1/1/album/2015/12/23/03/01/1450882882_02-6488-1450142522.jpg', '12499', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '1', '', '0', '1', 'anh-cuoi-tuyet-dep-giua-mua-hoa-da-lat');
INSERT INTO `vi_album` VALUES ('54', '1', null, ',54,', 'ẢNH CƯỚI HUYỀN ẢO CỦA CẶP ĐÔI BÁC SĨ', null, 'Nam và Hạnh chọn Đà Lạt với rừng thông lung linh trong nắng sớm, cùng nhiều cảnh đẹp thiên nhiên để ghi lại khoảnh khắc lãng mạn.', 'uploadv2/web/1/1/album/2015/12/24/02/11/1450923086_2a-2288-1444192817.jpg', '12525', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '1', '', '0', '1', 'anh-cuoi-huyen-ao-cua-cap-doi-bac-si');
INSERT INTO `vi_album` VALUES ('55', '1', null, ',54,', 'ẢNH CƯỚI ĐẸP LUNG LINH DỊP CUỐI NĂM', null, 'Bộ ảnh của cô dâu Thúy Nhi và Ngọc Thắng được thực hiện ở nhiều khung cảnh đẹp lãng mạn của thành phố ngàn hoa.', 'uploadv2/web/1/1/album/2015/12/24/02/33/1450924431_ikl0789-5662-1449740232.jpg', '12557', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '1', '', '0', '1', 'anh-cuoi-dep-lung-linh-dip-cuoi-nam');
INSERT INTO `vi_album` VALUES ('56', '1', null, ',54,', 'CẶP ĐÔI QUẬY TƯNG BỪNG VỚI ALBUM CỔ TÍCH', null, 'Cô dâu Mỹ Vân và chú rể Nguyên Bình tái hiện lại khoảng thời gian nghịch ngợm thời học sinh, pha chất cổ tích ấn tượng.', 'uploadv2/web/1/1/album/2015/12/24/03/08/1450926531_5-4367-1450346715.jpg', '12572', '1', '310', '310', '2015', '2015', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', null, '0', '1', '', '0', '1', 'cap-doi-quay-tung-bung-voi-album-co-tich');
INSERT INTO `vi_album` VALUES ('57', '1', null, ',54,', 'ẢNH CƯỚI GIỮA MÙA HOA ANH ĐÀO', null, 'Những bức ảnh cưới của đôi uyên ương Hiệp và Thảo được ghi lại trong thời gian đẹp nhất ở thành phố hoa.', 'uploadv2/web/1/1/album/2015/12/24/03/24/1450927466_eclair-joli-4-1434345382_1200x0.jpg', '12597', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '1', '', '0', '1', 'anh-cuoi-giua-mua-hoa-anh-dao');
INSERT INTO `vi_album` VALUES ('58', '1', null, ',54,', 'ẢNH CƯỚI LÃNG MẠN GIỮA KHÔNG GIAN XANH', null, 'Cặp đôi Mỹ Thoa - Thạnh Phú chọn không gian thiên nhiên xanh mát để ghi lại những khoảnh khắc kỷ niệm.', 'uploadv2/web/1/1/album/2015/12/24/03/54/1450929272_anh-2-1787-1444787259.jpg', '12611', '1', '310', '310', '2015', '2015', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '1', '', '0', '1', 'anh-cuoi-lang-man-giua-khong-gian-xanh');
