INSERT INTO `vi_album` VALUES ('61', '1', null, ',54,', 'UYÊN ƯƠNG BÊN NHAU TÌNH CẢM', null, 'Cách cặp đôi quan tâm, thể hiện tình yêu với nhau vừa tự nhiên, vừa lãng mạn, ngọt ngào.', 'uploadv2/web/1/1/album/2015/12/24/06/53/1450940001_img-9029-8739-1426826411.jpg', '12731', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '1', '', '0', '1', 'uyen-uong-ben-nhau-tinh-cam');
