INSERT INTO `vi_album` VALUES ('67', '1', null, ',54,', 'SẮC MÀU ĐÀ LẠT', null, 'Thiên nhiên và con người thật gần gũi, như luôn hòa nguyện, đan xen vào nhau, cũng giống như tình yêu của em và anh trong lúc này.', 'uploadv2/web/1/1/album/2015/12/24/08/29/1450945792_26-1420272086-5282-1420277867.jpg', '12861', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '1', '', '0', '1', 'sac-mau-da-lat');
INSERT INTO `vi_album` VALUES ('68', '1', null, ',54,', 'ĐÀ LẠT SƯƠNG MÙ', null, 'Cảm ơn anh, người đã mang đến cho em nụ cười, niềm hạnh phúc và hơn hết là chỗ dựa vững chắc của cuộc đời em.', 'uploadv2/web/1/1/album/2015/12/24/08/35/1450946153_dsc-6930-1413209627-4618-1413449043.jpg', '12874', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '1', '', '0', '1', 'da-lat-suong-mu');
