INSERT INTO `vi_album` VALUES ('72', '1', null, ',54,', 'CÔ DÂU ĐẸP DỊU DÀNG TRONG ẢNH CƯỚI', null, 'Cô dâu xinh đẹp và mộng mơ bên chú rể trong album cưới thực hiện tại thành phố ngàn hoa.', 'uploadv2/web/1/1/album/2015/12/24/09/08/1450948125_16-2458-1440431583.jpg', '12990', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '1', '', '0', '1', 'co-dau-dep-diu-dang-trong-anh-cuoi');
