INSERT INTO `vi_album` VALUES ('79', '1', null, ',54,', 'ALBUM CƯỚI TẠI MA RỪNG LỮ QUÁN ĐÀ LẠT', null, 'Ma Rừng Lữ Quán Đà Lạt - điểm du lịch với cái tên gọi ma mị và độc đáo sở hữu vẻ đẹp hoang sơ mà không kèm phần tinh tế đã cuốn hút nhiều du khách. Bầu trời trong, mặt hồ chỉ gợn nhẹ vài con sóng nhỏ, bên hồ vài cánh bướm dập dìu khoe sắc cùng ngan hoa. T', 'uploadv2/web/1/1/album/2015/12/25/02/05/1451009104_ma-rung-lu-quan-da-lat-30-77.jpg', '13156', '1', '310', null, '2015', null, '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '1', '', '0', '1', 'album-cuoi-tai-ma-rung-lu-quan-da-lat');
INSERT INTO `vi_album` VALUES ('80', '1', null, ',54,', 'ĐÀ LẠT, THÀNH PHỐ CỦA ƯỚC MƠ', null, 'Có người đã nói với tôi rằng khi nào đến ngày vui nhất của đời mình, nhất định cô ấy sẽ đi chụp hình cưới ở Đà Lạt. Có lẽ, chính những cái nắng nhẹ nhàng, không khí se lạnh, những dải sương mù huyền ảo đã làm cho người ta thêm yêu Đà Lạt.', 'uploadv2/web/1/1/album/2015/12/25/02/19/1451009977_da-lat-mong-mo-24.jpg', '13158', '1', '310', '310', '2015', '2015', '0000-00-00 00:00:00', '', '', '', '1', '{\"related_list\":\",,\",\"show\":0,\"show_quantity\":5,\"related_order\":1}', '{\"show\":1,\"show_quantity\":5,\"related_order\":1}', '0', '1', '', '0', '1', 'da-lat-xu-xo-cua-tinh-yeu');
