INSERT INTO `vi_info` VALUES ('70', null, '171', null, 'GIỚI THIỆU', '', '<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">C&ocirc;ng ty TNHH kỹ thuật điện An Sơn hoạt động trong lĩnh vực: Cung cấp thiết bị, sản phẩm v&agrave; giải ph&aacute;p cho điện c&ocirc;ng nghiệp, tự động h&oacute;a, đo lường v&agrave; điều khiển.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">C&ocirc;ng ty An Sơn ng&agrave;y c&agrave;ng đổi mới v&agrave; đ&atilde; tạo được chỗ đứng tr&ecirc;n thị trường Việt Nam. Với sự ph&aacute;t triển như hiện nay, ch&uacute;ng t&ocirc;i&nbsp;đ&atilde; đầu tư &amp; hợp t&aacute;c với c&aacute;c h&atilde;ng cung cấp thiết bị v&agrave; tự động h&oacute;a c&ocirc;ng nghiệp tr&ecirc;n thế giới như:</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">+Nh&agrave; ph&acirc;n phối ch&iacute;nh thức c&aacute;c thiết bị của h&atilde;ng DINKLE tại Việt Nam.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">+Đại l&yacute; của c&aacute;c h&atilde;ng: PHOENIX CONTACT, SIEMENS, SCHNEIDER ELECTRIC.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">I. SẢN PHẨM CHỦ YẾU</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">+Thiết bị đấu nối v&agrave; phụ kiện d&ugrave;ng trong tủ điện:</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">+PCB Terminal, (Domino cho board mạch)</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">+DOMINO t&eacute;p, DOMONI khối</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">+Bộ lập tr&igrave;nh PLC S7: 200, 300, 400, 1200, 1500</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">+Bộ nguồn, relay, thiết bị chống s&eacute;t</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">+Thiết bị chuyển đổi t&iacute;n hiệu</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">+Biến tần, khởi động mềm</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">+Đ&egrave;n b&aacute;o, c&ocirc;ng tắc, n&uacute;t nhấn, MCB, MCCB</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">II. MỤC TI&Ecirc;U PH&Aacute;T TRIỂN</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">- Mở rộng hoạt động kinh doanh với sản phẩm v&agrave; dịch vụ ng&agrave;y c&agrave;ng phong ph&uacute; đa dạng đ&aacute;p ứng c&aacute;c nhu cầu kh&aacute;c nhau của kh&aacute;ch h&agrave;ng.<br  />\r\n- Tiếp tục l&agrave; c&ocirc;ng ty đi đầu trong việc giới thiệu v&agrave; cung cấp c&aacute;c thiết bị v&agrave; giải ph&aacute;p tự động h&oacute;a c&ocirc;ng nghiệp mới tới tận tay người d&ugrave;ng cuối.<br  />\r\n- Giữ vững uy t&iacute;n, ng&agrave;y c&agrave;ng ho&agrave;n thiện v&agrave; ph&aacute;t triển hệ thống quản l&yacute; chuy&ecirc;n nghiệp, ph&aacute;t triển nh&acirc;n lực, gia tăng doanh thu, chiếm lĩnh thị phần v&agrave; n&acirc;ng cao gi&aacute; trị thương hiệu.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">Đội ngũ được t&agrave;o đạo v&agrave; hỗ trợ tốt nhất từ ch&iacute;nh h&atilde;ng, ch&uacute;ng t&ocirc;i cam kết cung cấp cho qu&yacute; kh&aacute;ch h&agrave;ng những sản phẩm chất lượng v&agrave; dịch vụ tốt nhất, chế độ hậu m&atilde;i v&agrave; gi&aacute; cả cạnh tranh.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">T&ecirc;n tiếng Anh: AN SON ELECTRICAL TECHNOLOGY Co. LTD</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">ĐC: 34-36 Đường DC6, P.Sơn Kỳ, Q.T&acirc;n Ph&uacute;, TP.HCM, VN&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">VPGD: 38 Đường DC6, P.Sơn Kỳ, Q.T&acirc;n Ph&uacute;, TP.HCM, VN&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">Tel: 08.3816.3343 Fax: 08.3816.4457&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">Email:&nbsp;<a href=\"mailto:info@dienanson.vn\" target=\"_blank\">info@dienanson.vn</a></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">Website:&nbsp;<a href=\"http://www.dienanson.vn/\">www.dienanson.vn</a></span></span></p>\r\n\r\n<p>&nbsp;</p>', '0', '1', '309', null, '1433410249', null, '', '', '', '0', '0', '', 'gioi-thieu');
