INSERT INTO `vi_info` VALUES ('73', null, '144', 'uploadv2/web/14/144/info/2015/06/11/09/40/1434033616_manulife-plaza-1.jpg', 'Manulife Việt Nam', 'Manulife Việt Nam là thành viên của Manulife Financial, Manulife Việt Nam tự hào là doanh nghiệp bảo hiểm nhân thọ nước ngoài đầu tiên có mặt tại Việt Nam từ năm 1999 và sở hữu tòa nhà trụ sở riêng có với giá trị đầu tư hơn 10 triệu USD. Với bề dày kinh nghiệm và uy tín toàn cầu, Manulife đặt mục tiêu trở thành công ty bảo hiểm nhân thọ chuyên nghiệp nhất tại Việt Nam.', '<h1><span style=\"font-size:16px\"><strong>Manulife Việt Nam</strong></span></h1>\r\n\r\n<p><span style=\"font-size:16px\"><strong><img alt=\"\" src=\"http://upload2.webbnc.vn/media/144/Manulife-plaza%20(1).jpg\" style=\"border-style:solid; border-width:3px; float:right; height:347px; margin:4px; width:300px\" /></strong></span></p>\r\n\r\n<div>\r\n<div style=\"text-align:justify\">\r\n<p><span style=\"font-size:14px\">L&agrave; th&agrave;nh vi&ecirc;n của Manulife Financial, Manulife Việt Nam tự h&agrave;o l&agrave; doanh nghiệp bảo hiểm nh&acirc;n thọ nước ngo&agrave;i đầu ti&ecirc;n c&oacute; mặt tại Việt Nam từ năm 1999 v&agrave; sở hữu t&ograve;a nh&agrave; trụ sở ri&ecirc;ng c&oacute; với gi&aacute; trị đầu tư hơn 10 triệu USD. Với bề d&agrave;y kinh nghiệm v&agrave; uy t&iacute;n to&agrave;n cầu, Manulife đặt mục ti&ecirc;u trở th&agrave;nh c&ocirc;ng ty bảo hiểm nh&acirc;n thọ chuy&ecirc;n nghiệp nhất tại Việt Nam.<br  />\r\n<br  />\r\nManulife Việt Nam hiện đang cung cấp một danh mục c&aacute;c sản phẩm đa dạng từ sản phẩm bảo hiểm truyền thống đến sản phẩm bảo hiểm sức khoẻ, gi&aacute;o dục, li&ecirc;n kết đầu tư, hưu tr&iacute;&hellip; cho kh&aacute;ch h&agrave;ng của hơn 480.000 hợp đồng th&ocirc;ng qua đội ngũ đại l&yacute; h&ugrave;ng hậu v&agrave; chuy&ecirc;n nghiệp tại 32 văn ph&ograve;ng tr&ecirc;n 25 tỉnh th&agrave;nh cả nước.&nbsp;<br  />\r\n<br  />\r\nB&ecirc;n cạnh k&ecirc;nh ph&acirc;n phối truyền thống qua đại l&yacute;, Manulife Việt Nam đa dạng h&oacute;a hoạt động kinh doanh bằng việc c&ugrave;ng c&aacute;c đối t&aacute;c ng&acirc;n h&agrave;ng triển khai k&ecirc;nh ph&acirc;n phối sản phẩm bảo hiểm qua ng&acirc;n h&agrave;ng (bancassurance) nhằm đ&aacute;p ứng nhu cầu ng&agrave;y c&agrave;ng đa dạng của thị trường. Ngo&agrave;i ra, Manulife Việt Nam l&agrave; doanh nghiệp bảo hiểm nh&acirc;n thọ duy nhất phối hợp với Trung Ương Hội li&ecirc;n hiệp Phụ nữ Việt Nam triển khai chương tr&igrave;nh bảo hiểm vi m&ocirc; d&agrave;nh cho phụ nữ c&oacute; thu nhập thấp với mong muốn c&ugrave;ng Ch&iacute;nh phủ phổ cập bảo hiểm nh&acirc;n thọ đến c&aacute;c gia đ&igrave;nh Việt Nam, g&oacute;p phần thực hiện c&aacute;c ch&iacute;nh s&aacute;ch an sinh x&atilde; hội, x&oacute;a đ&oacute;i giảm ngh&egrave;o v&agrave; tạo &yacute; thức tiết kiệm cho người d&acirc;n.</span></p>\r\n\r\n<div><br  />\r\n<span style=\"font-size:14px\">Với hơn 15 năm hoạt động tại Việt Nam, Manulife Việt Nam đ&atilde; kh&ocirc;ng ngừng ph&aacute;t triển x&acirc;y dựng vững chắc uy t&iacute;n trong thị trường, khẳng định vị tr&iacute; l&agrave; một trong ba c&ocirc;ng ty bảo hiểm nh&acirc;n thọ h&agrave;ng đầu tại Việt Nam.</span>\r\n\r\n<div>&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>', '0', '1', '264', '264', '1434033297', '1434091602', '', '', '', '0', '0', '', 'manulife-viet-nam');
INSERT INTO `vi_info` VALUES ('74', null, '166', 'uploadv2/web/16/166/info/2015/06/13/09/40/1434163200_logo.jpg', 'Về Chúng Tôi', 'Shopthit.com là nhà cung cấp các loại thực phẩm nhập khẩu cao cấp từ Úc, Mỹ. \r\nShopthit.com cam kết mang đến cho ngươi tiêu dùng những sản phẩm thịt bò tươi, được giết mổ trực tiếp từ Úc, Mỹ với giá bình dân nhất.', '<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\"><u><strong>Shopthit.com</strong></u>&nbsp;l&agrave; nh&agrave; cung cấp c&aacute;c loại thực phẩm nhập khẩu cao cấp từ &Uacute;c, Mỹ.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\"><u><strong>Shopthit.com</strong></u>&nbsp;cam kết mang đến cho ngươi ti&ecirc;u d&ugrave;ng những sản phẩm thịt b&ograve; tươi, được giết mổ trực tiếp từ &Uacute;c, Mỹ với gi&aacute; b&igrave;nh d&acirc;n nhất.</span></span></p>\r\n\r\n<ul>\r\n <li>\r\n <p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Đối t&aacute;c của&nbsp;<u><strong>Shopthit.com</strong></u>&nbsp;l&agrave; những l&ograve; giết mổ v&agrave; nh&agrave; xuất khẩu lớn nhất của &Uacute;c, Mỹ.</span></span></p>\r\n </li>\r\n <li>\r\n <p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Thịt b&ograve; v&agrave; thịt cừu tươi nhập khẩu của&nbsp;<u><strong>Shopthit.com</strong></u>&nbsp;được cấp chứng chỉ của cơ quan gi&aacute;m s&aacute;t AUS-MEAT v&agrave; cơ quan kiểm dịch AIQS của &Uacute;c cũng như đầy đủ giấy chứng nhận nguồn gốc, giấy chứng nhận chất lượng của Cơ quan Th&uacute; y v&ugrave;ng 6.</span></span></p>\r\n </li>\r\n <li>\r\n <p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:16px\">Đến với&nbsp;<u><strong>Shopthit.com</strong></u>, bạn kh&ocirc;ng cần phải đi đ