INSERT INTO `vi_news` VALUES ('1815', null, '1', ',,', ',286,', 'uploadv2/web/1/1/news/2016/01/06/06/31/1452061888_2.jpg', 'ĐÀ LẠT THÁNG 4 - NỖI NHỚ MIÊN MAN MÙA PHƯỢNG TÍM', 'da-lat-thang-4-noi-nho-mien-man-mua-phuong-tim', 'Đà Lạt nhiều hoa đến vậy nhưng sao phượng tím lại quyến rũ lạ kỳ. Màu tím mộng mơ mang nỗi buồn man mác ưu tư ấy như chất chứa cả hồn Đà Lạt vào những cánh hoa mỏng manh...', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><strong><em>Đ&agrave; Lạt nhiều hoa đến vậy nhưng sao phượng t&iacute;m lại quyến rũ lạ kỳ. M&agrave;u t&iacute;m mộng mơ mang nỗi buồn man m&aacute;c ưu tư ấy như chất chứa cả hồn Đ&agrave; Lạt v&agrave;o những c&aacute;nh hoa mỏng manh, mềm mại. Khi xu&acirc;n qua, sắc đ&agrave;o mai dần phai nhạt, đ&acirc;u đ&oacute;, tr&ecirc;n những t&aacute;n c&acirc;y xanh biếc, phượng đ&atilde; thấp tho&aacute;ng m&agrave;u hoa. Để rồi khi th&aacute;ng 4 ung dung g&otilde; cửa, khung trời Đ&agrave; Lạt đ&atilde; phủ nhuộm sắc t&iacute;m tự bao giờ.</em></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Khung trời tím nơi Đà Lạt khi tháng 4 sang\" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%204/1.jpg\" style=\"border:0px; height:494px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Khung trời tím nơi Đà Lạt khi tháng 4 sang\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>Khung trời t&iacute;m nơi Đ&agrave; Lạt khi th&aacute;ng 4 sang - Ảnh: Trương Ngọc Thụy</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">Tự bao giờ? C&oacute; lẽ người bộ h&agrave;nh cũng kh&ocirc;ng biết r&otilde;, chỉ nhớ rằng mỗi độ tết qua, bất chợt m&agrave;u hoa t&iacute;m lại &ugrave;a về phủ rợp tr&ecirc;n những con đường Đ&agrave; Lạt, khi ấy đất trời cao nguy&ecirc;n đ&atilde; v&agrave;o độ th&aacute;ng 4. M&agrave;u hoa t&iacute;m khẽ kh&agrave;ng v&agrave; e ấp chứ chẳng rực rỡ như hoa đ&agrave;o hồng thắm hay d&atilde; quỳ v&agrave;ng &oacute;ng ả, tươi vui, hoa tự chọn một m&agrave;u thật kh&aacute;c.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Hoa phượng tím phủ rợp trên con những con đường Đà Lạt \" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%204/2.jpg\" style=\"border:0px; height:533px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Hoa phượng tím phủ rợp trên con những con đường Đà Lạt \" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>Hoa phượng t&iacute;m phủ rợp tr&ecirc;n con những con đường - &nbsp;Ảnh: Trương Ngọc Thụy</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">Hoa phượng t&iacute;m dịu d&agrave;ng phủ khắp nẻo đường trung t&acirc;m th&agrave;nh phố tạo n&ecirc;n một kh&ocirc;ng gian rất thơ trong l&ograve;ng phố thị. Phượng t&iacute;m khoe sắc rợp trời như một lời ngỏ duy&ecirc;n d&aacute;ng, đầy ẩn &yacute;: &ldquo;Về đ&acirc;y, Đ&agrave; Lạt th&aacute;ng 4!&rdquo;, về để ngắm nh&igrave;n, để cảm nhận v&agrave; để y&ecirc;u một Đ&agrave; Lạt khoảnh khắc giao m&ugrave;a đầy xao xuyến.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Màu hoa tím dịu dàng trong lòng Đà Lạt\" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%204/3.jpg\" style=\"border:0px; height:533px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Màu hoa tím dịu dàng trong lòng Đà Lạt\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>M&agrave;u hoa t&iacute;m dịu d&agrave;ng trong l&ograve;ng Đ&agrave; Lạt - Ảnh: Trương Ngọc Thụy</em></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Đà Lạt tháng 4 - Màu hoa phượng tím xen lẫn với những sắc hoa rực rỡ \" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%204/4.jpg\" style=\"border:0px; height:449px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Đà Lạt tháng 4 - Màu hoa phượng tím xen lẫn với những sắc hoa rực rỡ \" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>M&agrave;u hoa phượng t&iacute;m xen lẫn với những sắc hoa rực rỡ - &nbsp;Ảnh: Trương Ngọc Thụy</em></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">Mỗi Lần bước ch&acirc;n qua những con đường mọc nhiều hoa phượng t&iacute;m như: L&ecirc; Hồng Gấm, B&ugrave;i Thị Xu&acirc;n,Nguyễn Thị Minh Khai,... du kh&aacute;ch sẽ phải dừng gi&acirc;y l&aacute;t trong sự ngỡ ng&agrave;ng th&iacute;ch th&uacute;. Như mới vừa t&igrave;m được khoảng lặng mộng mơ tr&ecirc;n con phố nhộn nhịp, đ&ocirc;ng người qua kẻ lại, người lữ kh&aacute;ch đắm say trong sắc m&agrave;u mi&ecirc;n man ấy chẳng muốn rời ch&acirc;n.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Góc phố lãng mạn chiều Đà Lạt tháng 4 \" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%204/5.jpg\" style=\"border:0px; height:533px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Góc phố lãng mạn chiều Đà Lạt tháng 4 \" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>G&oacute;c phố l&atilde;ng mạn chiều th&aacute;ng 4 - Ảnh: Trương Ngọc Thụy</em></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Đà Lạt tháng 4 - Sắc hoa nồng nàn ngây ngất khiến lữ khách ghé ngang cứ hoài lưu luyến\" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%204/6.jpg\" style=\"border:0px; height:800px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Đà Lạt tháng 4 - Sắc hoa nồng nàn ngây ngất khiến lữ khách ghé ngang cứ hoài lưu luyến\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>Sắc hoa nồng n&agrave;n ng&acirc;y ngất khiến lữ kh&aacute;ch gh&eacute; ngang cứ ho&agrave;i lưu luyến - &nbsp;</em><em>Ảnh: Trương Ngọc Thụy</em></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">Tr&ecirc;n những con dốc quanh co nơi triền n&uacute;i, những con đường, g&oacute;c phố nhỏ xinh, bất chợt lang thang gặp m&agrave;u phượng t&iacute;m, người lữ kh&aacute;ch kh&ocirc;ng thể ngăn tim m&igrave;nh th&ocirc;i rung động. Đ&agrave; Lạt th&aacute;ng 4 l&atilde;ng mạn v&agrave; hồn nhi&ecirc;n qu&aacute; đỗi! Phượng lấp đầy bức tranh an nhi&ecirc;n, trong trẻo bằng gam m&agrave;u t&iacute;m nhẹ nh&agrave;ng, s&acirc;u lắng lại v&ocirc; t&igrave;nh l&agrave;m cảnh vật th&ecirc;m m&ecirc;nh mang, khắc khoải. Sắc hoa t&iacute;m ấy kh&ocirc;ng rực rỡ cũng chẳng lung linh nhưng l&atilde;ng mạn v&agrave; nồng n&agrave;n ng&acirc;y ngất khiến lữ kh&aacute;ch gh&eacute; ngang cứ ho&agrave;i lưu luyến.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Người Đà Lạt lại càng yêu mùa hoa phượng tím\" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%204/7.jpg\" style=\"border:0px; height:608px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Người Đà Lạt lại càng yêu mùa hoa phượng tím\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>&hellip; người Đ&agrave; Lạt lại c&agrave;ng y&ecirc;u m&ugrave;a hoa phượng t&iacute;m.- &nbsp;&nbsp;Ảnh: Trương Ngọc Thụy</em></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">Người Đ&agrave; Lạt lại c&agrave;ng y&ecirc;u hoa phượng t&iacute;m, bao năm th&aacute;ng sống tr&ecirc;n mảnh đất cao nguy&ecirc;n l&agrave; bấy nhi&ecirc;u lần m&ugrave;a hoa phượng lại về, ấy vậy m&agrave; họ chưa bao giờ biết &ldquo;ch&aacute;n&rdquo;, chỉ thấy da diết nhớ khi Đ&agrave; Lạt th&aacute;ng 4. Tự hỏi rằng Đ&agrave; Lạt khi n&agrave;o chẳng c&oacute; m&ugrave;a hoa, sao phượng t&iacute;m lại khiến nhiều người nhớ nhung đến vậy?.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Phượng tím hòa sắc cùng ban trắng nơi phố nhỏ Đà Lạt\" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%204/8.jpg\" style=\"border:0px; height:533px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Phượng tím hòa sắc cùng ban trắng nơi phố nhỏ Đà Lạt\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>Phượng t&iacute;m h&ograve;a sắc c&ugrave;ng ban trắng nơi phố nhỏ - &nbsp;Ảnh: Trương Ngọc Thụy</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">M&ugrave;a phượng t&iacute;m khẽ kh&agrave;ng về lại mang theo những ồn &agrave;o cảm x&uacute;c, những k&yacute; ức của người đ&atilde; từng đi qua m&ugrave;a hoa ấy, sao c&oacute; thể n&agrave;o qu&ecirc;n. Những kỉ niệm theo m&agrave;u hoa cứ vội v&agrave;ng t&igrave;m đến như người bạn bao năm kh&ocirc;ng gặp mặt, c&oacute; buồn, c&oacute; vui, c&oacute; tiếc nuối, c&oacute; ngọt ng&agrave;o&hellip;tr&agrave;n đầy thanh &acirc;m cuộc sống.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Đà Lạt tháng 4 - Mùa hoa phượng tím tràn về bao ký ức\" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%204/9.jpg\" style=\"border:0px; height:1200px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Đà Lạt tháng 4 - Mùa hoa phượng tím tràn về bao ký ức\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>M&ugrave;a hoa phượng t&iacute;m tr&agrave;n về bao k&yacute; ức - Ảnh: Trương Ngọc Thụy</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">Những năm th&aacute;ng học tr&ograve; đ&atilde; từng gắn liền m&agrave;u phượng t&iacute;m, ng&acirc;y ng&ocirc; v&agrave; đẹp đến mộng mơ. Với người Đ&agrave; Lạt, c&oacute; ai chưa từng đi qua những m&ugrave;a hoa dịu d&agrave;ng như thế! Những con đường hoa phượng t&iacute;m trong k&yacute; ức mỗi người mỗi kh&aacute;c, mu&ocirc;n m&agrave;u v&agrave; &ldquo;rực rỡ&rdquo; đến nao l&ograve;ng. Bởi thế đừng hỏi tại sao người Đ&agrave; Lạt y&ecirc;u m&ugrave;a hoa phượng th&aacute;ng 4 đến vậy, k&yacute; ức l&agrave; điều đ&acirc;u dễ kiếm t&igrave;m ở một v&ugrave;ng đất lạ!</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">Lang thang tr&ecirc;n Đ&agrave; Lạt những ng&agrave;y sắp hạ, m&agrave;u t&iacute;m nhạt ngập tr&ecirc;n cung đường vắng l&agrave;m ta dễ nao l&ograve;ng, mi&ecirc;n man nhớ, d&ugrave; rằng ta chỉ l&agrave; lữ kh&aacute;ch đường xa. M&ugrave;a hoa phượng t&iacute;m đẹp v&agrave; nhiều hơn thế - m&ugrave;a hoa của những x&uacute;c cảm tr&ograve;n đầy.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Đà Lạt - mùa phượng tím tuổi học trò trong trẻo, tinh khôi đầy những mộng mơ\" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%204/10.jpg\" style=\"border:0px; height:533px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Đà Lạt - mùa phượng tím tuổi học trò trong trẻo, tinh khôi đầy những mộng mơ\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>M&ugrave;a phượng t&iacute;m tuổi học tr&ograve; trong trẻo, tinh kh&ocirc;i đầy những mộng mơ - Ảnh: Trương Ngọc Thụy</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">Nhiều người n&oacute;i rằng hoa phượng t&iacute;m biểu trưng cho Th&agrave;nh phố sương m&ugrave; Đ&agrave; Lạt. Trong vương quốc lo&agrave;i hoa với mu&ocirc;n v&agrave;n hương sắc, sao lại chọn phượng t&iacute;m mong manh? Phải chăng do vẻ đẹp đầy mơ mộng của m&agrave;u hoa t&iacute;m mỗi dịp th&aacute;ng 4 l&agrave;m nhiều người ngẩn ngơ, m&ecirc; đắm? Phải chăng m&agrave;u hoa ấy như g&oacute;i g&eacute;m cả t&acirc;m hồn người Đ&agrave; Lạt: l&atilde;ng mạn v&agrave; suy tư? Phải chăng ẩn trong m&agrave;u hoa ấy l&agrave; h&igrave;nh ảnh một Đ&agrave; Lạt diễm lệ, xinh đẹp v&agrave; man m&aacute;c buồn vương? Đ&agrave; Lạt th&aacute;ng 4 lang thang trong khung trời phượng t&iacute;m m&ecirc;nh mang v&agrave; những cảm x&uacute;c tuyệt vời như thế.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Đà Lạt - Có những kỉ niệm dịu êm\" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%204/11.jpg\" style=\"border:0px; height:1200px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Đà Lạt - Có những kỉ niệm dịu êm\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>C&oacute; những kỉ niệm dịu &ecirc;m&hellip; - Ảnh: Trương Ngọc Thụy</em></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"và cả những nỗi buồn man mác đi qua màu phượng tím Đà Lạt\" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%204/12.jpg\" style=\"border:0px; height:533px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"và cả những nỗi buồn man mác đi qua màu phượng tím Đà Lạt\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>&hellip; v&agrave; cả những nỗi buồn man m&aacute;c đi qua m&agrave;u phượng t&iacute;m - &nbsp;Ảnh: Trương Ngọc Thụy</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">Bỗng thấy rằng Đ&agrave; Lạt được thi&ecirc;n nhi&ecirc;n &ldquo;y&ecirc;u thương&rdquo; qu&aacute; đỗi, m&ugrave;a hoa n&agrave;o cũng xinh đẹp đến m&ecirc; say, ng&otilde; ng&aacute;ch n&agrave;o cũng l&agrave;m nao l&ograve;ng người lữ kh&aacute;ch. Đ&agrave; Lạt th&aacute;ng 4, đi trong mi&ecirc;m man nỗi nhớ v&agrave; m&agrave;u hoa t&iacute;m phủ ngập tr&ecirc;n vai ta thấy l&ograve;ng lắng đọng những ưu tư &nbsp;dịu d&agrave;ng, trong trẻo.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>Con đường phượng t&iacute;m chiều nay đổ</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>B&oacute;ng l&aacute; che nghi&ecirc;ng một g&oacute;c đời</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>Nghe trong c&otilde;i nhớ niềm xa x&oacute;t</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>Chợt tho&aacute;ng hiện về dấu y&ecirc;u ơi!</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>(Mi&ecirc;n Thụy)</em></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em><strong>Hạ Nhi&ecirc;n- Mytour.vn</strong></em></span></span></p>', '0', '1', '310', null, '1452061888', null, '', '', '', '0', '0', '', '', '0', '', '1', '{\"news_id\":0,\"status\":\"on\",\"show_quantity\":\"5\",\"show_sort\":\"1\",\"show_type\":\"list\",\"show_news\":\"tin_ngaunhien\"}', null);
INSERT INTO `vi_news` VALUES ('1816', null, '1', ',1809,1810,1811,1812,1813,1814,1815,', ',286,', 'uploadv2/web/1/1/news/2016/01/07/01/48/1452131301_6.jpg', 'ĐÀ LẠT THÁNG 6 - KHÚC CA MÙA HẠ ĐẦY TRONG TRẺO', 'da-lat-thang-6-khuc-ca-mua-ha-day-trong-treo', 'Đà Lạt những ngày mùa hạ như bản hòa ca tuyệt vời, trong trẻo của đất trời len vào lòng người dư âm nhẹ nhàng mà lắng đọng khôn nguôi. Những thanh âm ấy chỉ có thể nhìn ngắm và cảm nhận bằng chính rung động tâm hồn...', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><strong><em>Đ&agrave; Lạt những ng&agrave;y m&ugrave;a hạ như bản h&ograve;a ca tuyệt vời, trong trẻo của đất trời len v&agrave;o l&ograve;ng người dư &acirc;m nhẹ nh&agrave;ng m&agrave; lắng đọng kh&ocirc;n ngu&ocirc;i. Những thanh &acirc;m ấy chỉ c&oacute; thể nh&igrave;n ngắm v&agrave; cảm nhận bằng ch&iacute;nh rung động t&acirc;m hồn. C&oacute; thể với mỗi người, hạ Đ&agrave; Lạt đơn giản chỉ l&agrave; khoảng thời gian th&aacute;ng 6 nhưng với ai đ&oacute; đ&atilde; tr&oacute;t l&ograve;ng y&ecirc;u n&eacute;t tinh kh&ocirc;i của hạ th&igrave; th&aacute;ng 6 ấy l&agrave; cả một khung trời tuyệt diệu trong tim.</em></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">Khi những tia nắng lạnh đ&atilde; bắt đầu kết th&uacute;c, Đ&agrave; Lạt chuyển m&igrave;nh bước sang m&ugrave;a sương bạc với những biển m&acirc;y trắng d&agrave;y đặc phủ khắp đất trời phố n&uacute;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">Đ&agrave; Lạt th&aacute;ng 6 - Những ng&agrave;y m&acirc;y cuộn. Một m&ugrave;a săn m&acirc;y lại rạo rực bắt đầu.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">Những ng&agrave;y n&agrave;y dậy thật sớm tầm 4 giờ s&aacute;ng, rong ruổi chạy trong hơi sương v&agrave; gi&oacute; lạnh tinh mơ để t&igrave;m điểm săn m&acirc;y, người nhiếp ảnh cũng đ&atilde; thấy th&uacute; vị lắm rồi. Để ngắm được cảnh m&acirc;y cuộn khắp n&uacute;i đồi, th&agrave;nh phố, phải đi đến những nơi cao v&agrave; tho&aacute;ng đ&atilde;ng tầm mắt như đỉnh Langbiang, H&ograve;n Bồ hoặc đường v&agrave;o Trại M&aacute;t,...</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Biển mây huyền ảo, mộng mơ những ngày Đà Lạt bước vào đầu hạ \" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%206/1.jpg\" style=\"border:0px; height:416px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Biển mây huyền ảo, mộng mơ những ngày Đà Lạt bước vào đầu hạ \" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>Biển m&acirc;y huyền ảo, mộng mơ những ng&agrave;y Đ&agrave; Lạt bước v&agrave;o đầu hạ - Ảnh: thaona</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">Những ng&agrave;y đầu hạ, mặt trời vẫn c&ograve;n si&ecirc;ng dậy sớm n&ecirc;n biển m&acirc;y biến chuyển rất nhanh v&agrave; chưa hẳn l&agrave; tuyệt đẹp. Thế nhưng khoảnh khắc đứng tr&ecirc;n đỉnh n&uacute;i trong những ng&agrave;y m&acirc;y cuộn đầu ti&ecirc;n mới tinh kh&ocirc;i v&agrave; th&iacute;ch th&uacute; l&agrave;m sao. Khung cảnh huyền ảo, bồng bềnh bềnh trong ph&uacute;t gi&acirc;y n&agrave;y cũng đ&atilde; đủ đẹp đến nao l&ograve;ng. Tr&ecirc;n những ngọn n&uacute;i hoang sơ, h&ugrave;ng vĩ, buổi sớm mai trong l&agrave;nh hiện ra trước mắt l&uacute;c n&agrave;y như một bức tranh thơ, ảo huyền đến m&ecirc; hoặc. Dường như chỉ nh&igrave;n th&ocirc;i đ&acirc;u đ&atilde; đủ, ta dang tay như muốn &ocirc;m trọn cả đất trời m&ecirc;nh m&ocirc;ng trắng x&oacute;a, &ocirc;m v&agrave;o l&ograve;ng th&aacute;ng 6 m&ugrave;a m&acirc;y đầu ti&ecirc;n.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Bức tranh Đà Lạt thơ mộng, huyền ảo đến mê say\" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%206/2.jpg\" style=\"border:0px; height:497px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Bức tranh Đà Lạt thơ mộng, huyền ảo đến mê say\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>Bức tranh Đ&agrave; Lạt thơ mộng, huyền ảo đến m&ecirc; say - Ảnh: thaona</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">Rồi đến khi những tia nắng đầu ti&ecirc;n bắt đầu chạm ng&otilde;, kh&ocirc;ng gian phủ nhuộm một m&agrave;u v&agrave;ng lấp l&aacute;nh, tho&aacute;ng ẩn hiện trong l&agrave;n sương sớm ch&ugrave;ng ch&igrave;nh chưa muốn tan đi. Những con đường Đ&agrave; Lạt l&uacute;c n&agrave;y vẫn c&ograve;n ch&igrave;m trong l&agrave;n sương mỏng manh mờ ảo, n&eacute;t y&ecirc;n b&igrave;nh phủ ngập kh&ocirc;ng gian l&agrave;m người đi ao ước thời gian c&oacute; thể dừng trong gi&acirc;y l&aacute;t th&ocirc;i cũng đủ thỏa l&ograve;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Những con đường mát lành, an nhiên trong sương sớm Đà Lạt \" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%206/3.jpg\" style=\"border:0px; height:472px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Những con đường mát lành, an nhiên trong sương sớm Đà Lạt \" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>Những con đường m&aacute;t l&agrave;nh, an nhi&ecirc;n trong sương sớm Đ&agrave; Lạt - Ảnh: thaona</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">&Aacute;nh nắng nhạt dịu d&agrave;ng l&ecirc;n, len qua kẽ l&aacute;, trải d&agrave;i tr&ecirc;n những đồi th&ocirc;ng xanh bạt ng&agrave;n rồi bao phủ khắp c&aacute;c cung đường, ng&otilde; ng&aacute;ch. Nắng hạ sớm mai trong trẻo v&agrave; mỏng manh như voan, lụa, nhẹ nh&agrave;ng, ấm &aacute;p phủ tr&ecirc;n vai &aacute;o như một lời ch&agrave;o duy&ecirc;n d&aacute;ng, dễ thương. Những ng&agrave;y phất phơ t&agrave; &aacute;o trắng đến trường buổi sớm, bước đi trong m&ugrave;a hạ c&oacute; nắng v&agrave; sương, c&ograve;n g&igrave; l&atilde;ng mạn v&agrave; tinh kh&ocirc;i hơn thế nữa.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Đà Lạt tháng 6 - Còn gì lãng mạn, tinh khôi hơn mùa hạ chốn này nữa chứ\" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%206/4.jpg\" style=\"border:0px; height:534px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Đà Lạt tháng 6 - Còn gì lãng mạn, tinh khôi hơn mùa hạ chốn này nữa chứ\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>C&ograve;n g&igrave; l&atilde;ng mạn, tinh kh&ocirc;i hơn m&ugrave;a hạ chốn n&agrave;y nữa chứ? - Ảnh: thaona</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">Đ&agrave; Lạt th&aacute;ng 6 như một bức tranh tr&agrave;n trề nhựa sống. Khắp kh&ocirc;ng gian tr&agrave;n ngập m&agrave;u xanh c&acirc;y cỏ, tươi non, mơn mởn, hoa l&aacute; thi nhau khoe sắc thắm mu&ocirc;n m&agrave;u, mu&ocirc;n vẻ. Đi trong tiết trời đầu hạ, người ta dễ thấy thanh xu&acirc;n phơi phới trong l&ograve;ng, nh&igrave;n đ&acirc;u cũng thấy sắc m&agrave;u của sự rộn r&agrave;ng, tươi mới. Đ&agrave; Lạt trong &aacute;nh nắng v&agrave;ng rực rỡ đ&atilde; tho&aacute;t khỏi vẻ buồn man m&aacute;c, ưu tư, th&agrave;nh phố mộng mơ l&uacute;c n&agrave;y cũng tươi tắn, hồn nhi&ecirc;n như ch&iacute;nh những m&agrave;u hoa đang nở rộ khắp những lối đi.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Mùa hạ trong trẻo về trên thành phố ngàn hoa Đà Lạt\" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%206/5.jpg\" style=\"border:0px; height:533px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Mùa hạ trong trẻo về trên thành phố ngàn hoa Đà Lạt\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>M&ugrave;a hạ trong trẻo về tr&ecirc;n th&agrave;nh phố ng&agrave;n hoa &nbsp;- Ảnh: Trương Ngọc Thụy</em></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Đà Lạt mùa này là một bức tranh đầy rực rỡ \" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%206/6.jpg\" style=\"border:0px; height:450px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Đà Lạt mùa này là một bức tranh đầy rực rỡ \" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>Đ&agrave; Lạt m&ugrave;a n&agrave;y l&agrave; một bức tranh đầy rực rỡ - Ảnh: Trương Ngọc Thụy</em></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">Những ng&agrave;y hạ mướt xanh, dạo chơi b&ecirc;n hồ Xu&acirc;n Hương, ngắm nh&igrave;n những c&aacute;nh hoa đua chen sắc thắm ven đường, t&igrave;m g&oacute;c qu&aacute;n quen bạn b&egrave; đ&ocirc;i ba c&acirc;u chuyện, hay chỉ muốn lặng y&ecirc;n tận hưởng sự b&igrave;nh nhi&ecirc;n, trong trẻo của đất trời &hellip; tất cả đều dịu d&agrave;ng len v&agrave;o t&acirc;m khảm một cảm gi&aacute;c an y&ecirc;n kh&oacute; thể gọi t&ecirc;n.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Đà Lạt tháng 6 - Một buổi sáng an nhiên hạ về xanh mướt.\" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%206/7.jpg\" style=\"border:0px; height:534px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Đà Lạt tháng 6 - Một buổi sáng an nhiên hạ về xanh mướt.\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>Một buổi s&aacute;ng an nhi&ecirc;n hạ về xanh mướt. - Ảnh: Viet Thanh</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">Đ&agrave; Lạt m&ugrave;a n&agrave;y như một c&ocirc; học tr&ograve; tươi tắn, hồn nhi&ecirc;n, vui vẻ đ&oacute; rồi cũng dễ buồn v&ocirc; cớ. Những cơn mưa th&aacute;ng 6 đến kh&ocirc;ng hẹn trước điều g&igrave;, c&oacute; khi trời đang hửng nắng ban trưa lại vấn v&iacute;t sương mờ v&agrave; cơn mưa bất chợt &ugrave;a về rất vội. Những chiều mưa lại trả về cho th&agrave;nh phố ch&uacute;t mộng mơ, s&acirc;u lắng v&agrave; man m&aacute;c nhớ. Thế mới thấy Đ&agrave; Lạt m&ugrave;a hạ cũng tinh tế lắm, như c&ocirc; học tr&ograve; mới lớn nay đ&atilde; biết buồn vương.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Đà Lạt mùa hè có những cơn mưa bất chợt\" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%206/8.jpg\" style=\"border:0px; height:571px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Đà Lạt mùa hè có những cơn mưa bất chợt\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>Đ&agrave; Lạt m&ugrave;a h&egrave; c&oacute; những cơn mưa bất chợt&hellip; - Ảnh: Trương Ngọc Thụy</em></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Đà Lạt tháng 6 - như cô học trò đã biết buồn vương\" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%206/9.jpg\" style=\"border:0px; height:532px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Đà Lạt tháng 6 - như cô học trò đã biết buồn vương\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>&hellip; như c&ocirc; học tr&ograve; đ&atilde; biết buồn vương &nbsp;- Ảnh: Trương Ngọc Thụy</em></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">Th&aacute;ng 6 về, những k&yacute; ức lại mặc nhi&ecirc;n g&otilde; cửa khi chợt thấy thấp tho&aacute;ng đ&acirc;u đ&oacute; m&agrave;u &aacute;o trắng tinh kh&ocirc;i. Ai cũng c&oacute; khoảnh khắc chia tay t&agrave; &aacute;o mộng mơ, rời xa trường lớp v&agrave; kh&eacute;p lại khoảng trời học tr&ograve; ng&acirc;y ng&ocirc;, nhiều dư vị trong những ng&agrave;y hạ về như thế.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Đà Lạt tháng 6 - Còn lại đây sân trường, ghế đá và nỗi nhớ\" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%206/10.jpg\" style=\"border:0px; height:534px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Đà Lạt tháng 6 - Còn lại đây sân trường, ghế đá và nỗi nhớ\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>C&ograve;n lại đ&acirc;y s&acirc;n trường, ghế đ&aacute; v&agrave; nỗi nhớ. &nbsp;- Ảnh: Kh&aacute;nh Vũ B&ugrave;i</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">Những c&ocirc; cậu học tr&ograve; trao nhau d&ograve;ng lưu b&uacute;t viết vội v&agrave;ng trong l&uacute;c ra chơi, những đ&oacute;a hoa, những nụ cười luyến lưu, bịn rịn, những c&aacute;i qu&agrave;ng vai thay lời n&oacute;i chia tay&hellip;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">Đ&agrave; Lạt th&aacute;ng 6 vẫn rực rỡ m&agrave;u hoa, chỉ c&oacute; học tr&ograve; cuối cấp mới hiểu được những nỗi niềm tr&ecirc;n khoảng s&acirc;n trường đầy nắng, tr&ecirc;n con đường đi học đ&atilde; th&acirc;n quen, tr&ecirc;n đ&ocirc;i mắt thầy, mắt bạn vấn vương những điều kh&ocirc;ng thể n&oacute;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Trên con đường Đà Lạt thơ mộng năm nào, tôi và bạn\" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%206/11.jpg\" style=\"border:0px; height:533px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Trên con đường Đà Lạt thơ mộng năm nào, tôi và bạn\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>Tr&ecirc;n con đường Đ&agrave; Lạt thơ mộng năm n&agrave;o, t&ocirc;i v&agrave; bạn,... &nbsp;-Ảnh: Trương Ngọc Thụy</em></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Đà Lạt - chúng ta giờ đã đi hết chặng đường rồi\" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%206/12.jpg\" style=\"border:0px; height:534px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Đà Lạt - chúng ta giờ đã đi hết chặng đường rồi\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>... ch&uacute;ng ta giờ đ&atilde; đi hết chặng đường rồi! - Ảnh: Kh&aacute;nh Vũ B&ugrave;i</em></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">Những bức ảnh kỷ yếu ra đời trong những ng&agrave;y Đ&agrave; Lạt nhiều thanh &acirc;m, s&acirc;u lắng v&agrave; trong trẻo như một kh&uacute;c ca về tuổi thanh xu&acirc;n. Tr&ecirc;n con đường Đ&agrave; Lạt thơ mộng năm n&agrave;o, t&ocirc;i v&agrave; bạn, thời gian đ&atilde; tr&ocirc;i qua nhanh đến thế, ch&uacute;ng ta giờ đ&atilde; đi hết chặng đường rồi! V&agrave; Đ&agrave; Lạt th&aacute;ng 6, m&ugrave;a chia tay năm n&agrave;o sẽ h&oacute;a th&agrave;nh k&yacute; ức.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Những bức ảnh kỷ yếu ra đời trong những ngày Đà Lạt nhiều thanh âm, sâu lắng và trong trẻo\" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%206/13.jpg\" style=\"border:0px; height:534px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Những bức ảnh kỷ yếu ra đời trong những ngày Đà Lạt nhiều thanh âm, sâu lắng và trong trẻo\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>Những bức ảnh kỷ yếu ra đời trong những ng&agrave;y Đ&agrave; Lạt nhiều thanh &acirc;m, s&acirc;u lắng v&agrave; trong trẻo - Ảnh: Kh&aacute;nh Vũ B&ugrave;i</em></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><img alt=\"Đà Lạt - Liệu những vòng xe ấy có thể đưa ta về với khung trời trong vắt năm nào\" src=\"http://admin.thaodalat.com/cdn/media/1/News/Dalat%20thang%206/14.jpg\" style=\"border:0px; height:533px; margin:0px; max-width:700px; padding:0px; width:800px\" title=\"Đà Lạt - Liệu những vòng xe ấy có thể đưa ta về với khung trời trong vắt năm nào\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em>Liệu những v&ograve;ng xe ấy c&oacute; thể đưa ta về với khung trời trong vắt năm n&agrave;o? -&nbsp;</em><em>Ảnh: Trương Ngọc Thụy</em></span></span></p>\r\n\r\n<div style=\"position:relative;padding-bottom:56.25%;padding-top:30px;height:0;overflow:hidden;\"><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"600\" src=\"//www.youtube.com/embed/4pw5CV6rM4o\" style=\"position: absolute;top: 0;left: 0;width: 100%;height: 100%;\" width=\"800\"></iframe></div>\r\n\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\">Đ&agrave; Lạt th&aacute;ng 6 l&agrave; g&igrave; nếu kh&ocirc;ng phải l&agrave; bản giao hưởng ngọt ng&agrave;o, đủ đầy &acirc;m sắc như tuổi thanh xu&acirc;n rực rỡ, tinh kh&ocirc;i, nhiều dư vị. Hạ về, Đ&agrave; Lạt như một bức tranh vui tươi, rộn r&agrave;ng, phủ ngập m&agrave;u xanh tr&agrave;n trề nhựa sống nhưng đ&acirc;u đ&oacute; lại chất chứa nhiều kỷ niệm hồn nhi&ecirc;n, man m&aacute;c nhớ thương. Một ng&agrave;y th&aacute;ng 6, tr&ecirc;n những con đường Đ&agrave; Lạt, chợt thấy &aacute;o trắng học tr&ograve; đạp xe đến lớp, l&ograve;ng tự hỏi rằng, liệu những v&ograve;ng xe ấy c&oacute; thể đưa ta về với khung trời trong vắt năm n&agrave;o?</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:16px\"><span style=\"font-family:verdana,geneva,sans-serif\"><em><strong>Hạ Nhi&ecirc;n - Mytour.vn</strong></em></span></span></p>', '0', '1', '310', null, '1452131301', null, '', '', '', '0', '0', '', '', '0', '', '1', '{\"news_id\":0,\"status\":\"on\",\"show_quantity\":\"5\",\"show_sort\":\"1\",\"show_type\":\"list\",\"show_news\":\"tin_ngaunhien\"}', null);
